mapper.select:
SELECT COUNT(*) CNT FROM FREE_LIST
WHERE MEMBER_NO = #{memberNo}
AND
FREE_STATUS = 'Y'

	AND




			FREE_TITLE LIKE #{val}


			FREE_CONTENT LIKE #{val}



		AND FREE_CATEGORY_NO = ${sc}

SELECT * FROM
FREE_LIST
WHERE FREE_STATUS = 'Y'
AND MEMBER_NO =
#{memberNo}

	AND




			FREE_TITLE LIKE #{val}


			FREE_CONTENT LIKE #{val}



		AND FREE_CATEGORY_NO = ${sc}




ORDER BY 



			FREE_READ_COUNT DESC,


			LIKE_COUNT DESC,




FREE_NO DESC

SELECT COUNT(*) CNT FROM QNA_LIST
WHERE MEMBER_NO = #{memberNo}
AND
QNA_STATUS IN('Y', 'S')

	AND




			QNA_TITLE LIKE #{val}


			QNA_CONTENT LIKE #{val}



		AND QNA_CATEGORY_NO = ${sc}

SELECT * FROM
QNA_LIST
WHERE QNA_STATUS IN('Y', 'S')
AND MEMBER_NO =
#{memberNo}

	AND




			QNA_TITLE LIKE #{val}


			QNA_CONTENT LIKE #{val}



		AND QNA_CATEGORY_NO = ${sc}



ORDER BY QNA_NO DESC

SELECT COUNT(*) CNT FROM
CHAT_ROOM
WHERE MEMBER_NO = #{memberNo}

SELECT CHAT_NO, A.CHAT_ROOM_NO, CHAT_CONTENT, CHAT_CREATE_DT, CHAT_READ_YN, (SELECT ATTRACTION_NM FROM CHAT_MANAGER_LIST B WHERE B.CHAT_ROOM_NO = A.CHAT_ROOM_NO
AND ATTRACTION_NM LIKE #{val}
) ATTRACTION_NM FROM (SELECT MAX(CHAT_NO) AS CHAT_NO, CHAT_ROOM_NO FROM CHAT_MESSAGE WHERE CHAT_ROOM_NO IN (SELECT DISTINCT CHAT_ROOM_NO FROM CHAT_ROOM WHERE MEMBER_NO = #{memberNo}) GROUP BY CHAT_ROOM_NO) A JOIN CHAT_MESSAGE USING(CHAT_NO)

		 SELECT COUNT(*) CNT FROM REPORT A
        JOIN REPORT_CATEGORY USING(REPORT_CATEGORY_NO)
        JOIN FREE_BOARD B ON A.REPORT_TYPE_NO = B.FREE_NO
        WHERE A.MEMBER_NO = #{memberNo}
        AND REPORT_STATUS = 'Y'
        AND FREE_STATUS = 'Y'

			AND




					REPORT_TITLE LIKE #{val}


					REPORT_CONTENT LIKE #{val}



				AND REPORT_CATEGORY_NO = ${sc}

		 SELECT * FROM REPORT A
        JOIN REPORT_CATEGORY USING(REPORT_CATEGORY_NO)
        JOIN FREE_BOARD B ON A.REPORT_TYPE_NO = B.FREE_NO
        WHERE A.MEMBER_NO = #{memberNo}
        AND REPORT_STATUS = 'Y'
        AND FREE_STATUS = 'Y'

			AND




					REPORT_TITLE LIKE #{val}


					REPORT_CONTENT LIKE #{val}



				AND REPORT_CATEGORY_NO = ${sc}



		ORDER BY REPORT_NO DESC

SELECT COUNT(*) CNT FROM FREE_REPLY A JOIN FREE_BOARD USING(FREE_NO) WHERE A.MEMBER_NO = #{memberNo} AND FREE_REPLY_STATUS = 'Y' AND FREE_STATUS = 'Y'
AND
FREE_REPLY_CONTENT LIKE #{val}

SELECT * FROM FREE_REPLY A JOIN FREE_BOARD USING(FREE_NO) WHERE A.MEMBER_NO = #{memberNo} AND FREE_REPLY_STATUS = 'Y' AND FREE_STATUS = 'Y'
AND
FREE_REPLY_CONTENT LIKE #{val}

		SELECT COUNT(*) CNT FROM REVIEW
        JOIN ATTRACTION_INFO USING(ATTRACTION_NO)
        WHERE MEMBER_NO = #{memberNo}
		AND REVIEW_STATUS = 'Y'

			AND




					ATTRACTION_NM LIKE #{val}


					REVIEW_CONTENT LIKE #{val}
